commits.content:
.highlight {
    background-color: rgba(46, 204, 113, 0.1);
    padding: 20px;
    border-radius: 5px;
    border-left: 5px solid var(--secondary-color);
    margin: 20px 0;
}

.section {
    margin-bottom: 40px;
    background-color: white;
    padding: 30px;
    border-radius: 8px;
    box-shadow: 0 2px 15px rgba(0,0,0,0.1);
}

.mission-vision {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
}

.mission, .vision {
    flex: 1;
    min-width: 300px;
    padding: 20px;
    border-radius: 8px;
}

.mission {
    background-color: rgba(52, 152, 219, 0.1);
    border-left: 5px solid var(--primary-color);
}

.vision {
    background-color: rgba(46, 204, 113, 0.1);
    border-left: 5px solid var(--secondary-color);
}

.card-container {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
    justify-content: center;
    margin: 30px 0;
}

.card {
    width: 300px;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    background-color: white;
    transition: transform 0.3s ease;
}

.card:hover {
    transform: translateY(-5px);
}

.card h3 {
    color: var(--primary-color);
    margin-top: 0;
}

table {
    width: 100%;
    border-collapse: collapse;
    margin: 20px 0;
}

th, td {
    border: 1px solid #ddd;
    padding: 12px;
    text-align: left;
}

th {
    background-color: var(--primary-color);
    color: white;
}

tr:nth-child(even) {
    background-color: #f2f2f2;
}
