.dao-diagram {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 30px 0;
}

.dao-circle {
    width: 200px;
    height: 200px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--carbon-color), var(--silicon-color));
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: bold;
    font-size: 24px;
    margin-bottom: 20px;
    position: relative;
}

.dao-connections {
    display: flex;
    justify-content: space-around;
    width: 100%;
    flex-wrap: wrap;
}

.dao-node {
    width: 150px;
    height: 100px;
    background-color: var(--light-color);
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 10px;
    border-radius: 8px;
    text-align: center;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    position: relative;
}

.dao-node::before {
    content: '';
    position: absolute;
    top: -20px;
    left: 50%;
    height: 20px;
    width: 2px;
    background-color: var(--dark-color);
}

.carbon {
    border-left: 5px solid var(--carbon-color);
}

.silicon {
    border-left: 5px solid var(--silicon-color);
}

.stakeholder {
    margin-bottom: 20px;
    padding: 15px;
    border-radius: 5px;
    background-color: var(--light-color);
}

.stakeholder h4 {
    margin-top: 0;
    color: var(--primary-color);
    border-bottom: 1px solid var(--primary-color);
    padding-bottom: 5px;
}
