/* Structure Section Styles */
.section {
  padding: 4rem 0;
  background-color: #f8f9fa;
}

.section-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.intro-text {
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 3rem;
  text-align: center;
}

/* Structure Cards Layout */
.structure-cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-bottom: 3rem;
}

.structure-card {
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  transition: transform 0.3s ease;
}

.structure-card:hover {
  transform: translateY(-5px);
}

.card-header {
  background: linear-gradient(135deg, #6c5ce7, #a29bfe);
  color: white;
  padding: 1.5rem;
  display: flex;
  align-items: center;
  gap: 1rem;
}

.card-header i {
  font-size: 1.5rem;
}

.card-header h3 {
  margin: 0;
  font-size: 1.5rem;
}

.card-content {
  padding: 1.5rem;
}

/* Structure List Styles */
.structure-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.structure-item {
  display: flex;
  align-items: flex-start;
  gap: 1rem;
  padding: 1rem 0;
  border-bottom: 1px solid #eee;
}

.structure-item:last-child {
  border-bottom: none;
}

.structure-item i {
  color: #6c5ce7;
  font-size: 1.2rem;
  margin-top: 0.2rem;
}

.item-content {
  flex: 1;
}

.item-content strong {
  display: block;
  color: #333;
  margin-bottom: 0.3rem;
}

.item-content span {
  color: #666;
  font-size: 0.9rem;
}

/* Value Distribution Styles */
.metric-section,
.incentive-section {
  margin-bottom: 1.5rem;
}

.metric-section h4,
.incentive-section h4 {
  color: #333;
  margin-bottom: 1rem;
  font-size: 1.2rem;
}

.metric-cards,
.incentive-cards {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;
}

.metric-card,
.incentive-card {
  background: #f8f9fa;
  padding: 1rem;
  border-radius: 8px;
  display: flex;
  align-items: flex-start;
  gap: 1rem;
}

.metric-card i,
.incentive-card i {
  color: #6c5ce7;
  font-size: 1.5rem;
}

.metric-content,
.incentive-content {
  flex: 1;
}

.metric-content strong,
.incentive-content strong {
  display: block;
  color: #333;
  margin-bottom: 0.3rem;
}

.metric-content p,
.incentive-content p {
  color: #666;
  margin: 0;
  font-size: 0.9rem;
}

/* Compliance and Risk Styles */
.compliance-section,
.risk-section {
  margin-bottom: 1.5rem;
}

.compliance-section h4,
.risk-section h4 {
  color: #333;
  margin-bottom: 1rem;
  font-size: 1.2rem;
}

.compliance-cards,
.risk-cards {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;
}

.compliance-card,
.risk-card {
  background: #f8f9fa;
  padding: 1rem;
  border-radius: 8px;
  display: flex;
  align-items: flex-start;
  gap: 1rem;
}

.compliance-card i,
.risk-card i {
  color: #6c5ce7;
  font-size: 1.5rem;
}

.compliance-content,
.risk-content {
  flex: 1;
}

.compliance-content strong,
.risk-content strong {
  display: block;
  color: #333;
  margin-bottom: 0.3rem;
}

.compliance-content p,
.risk-content p {
  color: #666;
  margin: 0;
  font-size: 0.9rem;
}

/* Responsive Design */
@media (min-width: 768px) {
  .metric-cards,
  .incentive-cards,
  .compliance-cards,
  .risk-cards {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* Icons */
@font-face {
  font-family: 'Icons';
  src: url('path-to-your-icons-font.woff2') format('woff2');
}

.icon-building:before { content: '\e900'; }
.icon-user:before { content: '\e901'; }
.icon-human:before { content: '\e902'; }
.icon-ai:before { content: '\e903'; }
.icon-contract:before { content: '\e904'; }
.icon-value:before { content: '\e905'; }
.icon-calculator:before { content: '\e906'; }
.icon-profit:before { content: '\e907'; }
.icon-reserve:before { content: '\e908'; }
.icon-fund:before { content: '\e909'; }
.icon-shield:before { content: '\e90a'; }
.icon-legal:before { content: '\e90b'; }
.icon-audit:before { content: '\e90c'; }
.icon-insurance:before { content: '\e90d'; }
.icon-security:before { content: '\e90e'; }

[class^="icon-"]:before,
[class*=" icon-"]:before {
  font-family: 'Icons';
  speak: never;
  font-style: normal;
  font-weight: normal;
  font-variant: normal;
  text-transform: none;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
