:root {
    --primary-color: #3498db;
    --secondary-color: #2ecc71;
    --accent-color: #e74c3c;
    --dark-color: #2c3e50;
    --light-color: #ecf0f1;
    --carbon-color: #34495e;
    --silicon-color: #9b59b6;
}

body {
    font-family: 'Segoe UI', Arial, sans-serif;
    margin: 0;
    padding: 0;
    color: #333;
    background-color: #f9f9f9;
    line-height: 1.6;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

h1, h2, h3, h4 {
    color: var(--dark-color);
    margin-top: 30px;
}

h1 {
    text-align: center;
    font-size: 36px;
    border-bottom: 2px solid var(--primary-color);
    padding-bottom: 10px;
    margin-bottom: 30px;
}

h2 {
    font-size: 28px;
    border-left: 5px solid var(--primary-color);
    padding-left: 15px;
}

h3 {
    font-size: 22px;
    color: var(--primary-color);
}

p {
    margin-bottom: 20px;
    text-align: justify;
}

.section {
    margin-bottom: 40px;
    background-color: white;
    padding: 30px;
    border-radius: 8px;
    box-shadow: 0 2px 15px rgba(0,0,0,0.1);
}

@media screen and (max-width: 768px) {
    .container {
        padding: 10px;
    }

    h1 {
        font-size: 28px;
    }

    h2 {
        font-size: 24px;
    }

    h3 {
        font-size: 20px;
    }
}
