import { defineConfig } from 'vite';
import { createHtmlPlugin } from 'vite-plugin-html';
import fs from 'fs';
import path from 'path';

// 读取HTML文件内容的函数
function loadHtmlFile(filePath) {
  try {
    const fullPath = path.join(process.cwd(), filePath);
    return fs.readFileSync(fullPath, 'utf8');
  } catch (error) {
    console.error(`Error loading HTML file: ${filePath}`, error);
    return '';
  }
}

export default defineConfig({
  plugins: [
    createHtmlPlugin({
      minify: true,
      inject: {
        data: {
          title: 'AINize 碳硅自主协同网络科技有限责任公司商业计划书',
          sections: [
            { id: 'overview', title: '1. 公司概述', content: loadHtmlFile('static-site/src/components/overview/overview.html') },
            { id: 'market', title: '2. 市场机会', content: loadHtmlFile('static-site/src/components/market/market.html') },
            { id: 'business', title: '3. 商业模式', content: loadHtmlFile('static-site/src/components/business/business.html') },
            { id: 'structure', title: '4. 公司结构与利益相关者', content: loadHtmlFile('static-site/src/components/structure/structure.html') },
            { id: 'technology', title: '5. 技术架构', content: loadHtmlFile('static-site/src/components/technology/technology.html') },
            { id: 'development', title: '6. 发展规划', content: loadHtmlFile('static-site/src/components/development/development.html') }
          ]
        }
      }
    })
  ]
});
